package basics;

public class ArithmaticoperaterEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		int c = a+b;
		int d = b-a;
		int e = a*b;
		int f = b/a;
		int g=a%b;
		
				
			System.out.println("The addition of a+b is "+c);
			
		
		System.out.println("The subtraction of b-a is "+d);
		
		
		System.out.println("The multiplication of a*b is "+e);

		
		System.out.println("The divition of b/a is "+f);

		
		System.out.println("The modulo of a%b is "+g);

		
		
		
		
		
		
		
	}

}
